        beki.assign(35, vector<int>());
        beki[0] = pqpq;
        for (int i = 0; i+1 < 35; ++i) {
            beki[i+1] = pl(beki[i], beki[i]);
        }
        auto res = solve(K);
        for (int i = 1; i < 10; ++i) {
            //cout << i << ": " << solve(i) << endl;
        }

