vector<int> bf(vector<int> p, vector<int> q, int k){
	if(k == 1) return p;
	for(int b = 2; b < k; b++){
		p = inv(p) * q;
		swap(p, q);
	}
	return q;
}

vector<int> expo(vector<int> p, int n){
	if(n == 0) return p * inv(p);
	if(n & 1) return p * expo(p, n-1);
	return expo(p*p, n/2);
}

vector<int> solve(vector<int> p, vector<int> q, int k){
	if(k <= 10) return bf(p, q, k);
	int c = (k - 4) / 6;
	vector<int> lhs = inv(q) * p * q * inv(p);
	vector<int> rhs = inv(lhs);
	return expo(lhs, c) * solve(p, q, k - c * 6) * expo(rhs, c);
}


