void Inv(int a[N],int R[N])
{
	int i;
	static int b[N];
	for(i=1;i<=n;i++) b[a[i]]=i;
	//printf("S%d %d %d\n",a[1],b[2],b[3]);
	for(i=1;i<=n;i++) R[i]=b[i];
}
void trans(int a[N],int b[N])
{
	int i;
	for(i=1;i<=n;i++) a[i]=b[a[i]];
}
void gnew(int a[N],int R[N],int cs)
{
	int i,x,top,rcs,now;
	static int nxt[N],vis[N],val[N],stk[N];
	memset(vis,0,sizeof(vis));
	for(i=1;i<=n;i++) nxt[i]=a[i];
	for(i=1;i<=n;i++)
	{
		if(vis[i]) continue;
		top=0;
		x=i;
		while(vis[x]==0) stk[top++]=x,vis[x]=1,x=nxt[x];
		rcs=cs%top;
		x=i;now=rcs;
		while(1)
		{
			val[x]=stk[now];
			if(nxt[x]==i) break;
			x=nxt[x];
			now++;if(now==top) now=0;
		}
	}
	for(i=1;i<=n;i++) R[i]=val[i];
}
