		ans[i] = i;
	}
	vector<int> T(N);
	vector<int> T2(N);
	for(int i=0;i<N;i++){
		T[i] = B[i];
	}
	while(K){
		if(K & 1){
			swap(ans,ans2);
			for(int i=0;i<N;i++){
				ans[i] = ans2[T[i]];
			}
		}
		swap(T,T2);
		for(int i=0;i<N;i++){
			T[i] = T2[T2[i]];
		}
		K /= 2;
	}
	for(int i=0;i<N;i++){
		A[i] = ans[i];
	}
