void Calc(){
	N = rei();
	long K = rel()-1;
	for(int i=0;i<N;i++){
		P[i] = rei()-1;
		PT[P[i]] = i;
	}
	for(int i=0;i<N;i++){
		Q[i] = rei()-1;
		QT[Q[i]] = i;
	}
	if(K % 6 == 0){
		Mul(buffer,Q,PT);
		Mul(buffer2,buffer,QT);
		Mul(buffer,buffer2,P);
		Pow(buffer,buffer,K/6);
		Rev(buffer4,buffer);
		Mul(buffer2,buffer,P);
		Mul(answer,buffer2,buffer4);
	}
	else if(K % 6 == 1){
		Mul(buffer,Q,PT);
		Mul(buffer2,buffer,QT);
		Mul(buffer,buffer2,P);
		Pow(buffer,buffer,K/6);
		Rev(buffer4,buffer);
		Mul(buffer2,buffer,Q);
		Mul(answer,buffer2,buffer4);
	}
	else if(K % 6 == 2){
		Mul(buffer,Q,PT);
		Mul(buffer2,buffer,QT);
		Mul(buffer,buffer2,P);
		Pow(buffer,buffer,K/6);
		Rev(buffer4,buffer);
		Mul(buffer2,buffer,Q);
		Mul(buffer,buffer2,PT);
		Mul(answer,buffer,buffer4);
	}
	else if(K % 6 == 3){
		Mul(buffer,Q,PT);
		Mul(buffer2,buffer,QT);
		Mul(buffer,buffer2,P);
		Pow(buffer,buffer,K/6);
		Rev(buffer4,buffer);
		Mul(buffer2,buffer,Q);
		Mul(buffer,buffer2,PT);
		Mul(buffer2,buffer,QT);
		Mul(answer,buffer2,buffer4);
	}
	else if(K % 6 == 4){
		Mul(buffer,Q,PT);
		Mul(buffer2,buffer,QT);
		Mul(buffer,buffer2,P);
		Pow(buffer,buffer,K/6);
		Rev(buffer4,buffer);
		Mul(buffer2,buffer,Q);
		Mul(buffer,buffer2,PT);
		Mul(buffer2,buffer,QT);
		Mul(buffer,buffer2,P);
		Mul(buffer2,buffer,QT);
		Mul(answer,buffer2,buffer4);
	}
	else{
		Mul(buffer,Q,PT);
		Mul(buffer2,buffer,QT);
		Mul(buffer,buffer2,P);
		Pow(buffer,buffer,K/6);
		Rev(buffer4,buffer);
		Mul(buffer2,buffer,Q);
		Mul(buffer,buffer2,PT);
		Mul(buffer2,buffer,QT);
		Mul(buffer,buffer2,P);
		Mul(buffer2,buffer,P);
		Mul(buffer,buffer2,QT);
		Mul(answer,buffer,buffer4);
	}
	for(int i=0;i<N;i++){
		cout << answer[i]+1;
		if(i == N-1){
			cout << endl;
		}
		else{
			cout << " ";
		}
	}
}
