template<typename T>
vector<int> binpow(T&& p, int k) {
    vector<int> ans(n);
    for (int i = 0; i < n; ++i) {
        ans[i] = i;
    }
    while (k) {
        if (k & 1) {
            ans = mult(p, ans);
        }
        p = mult(p, p);
        k >>= 1;
    }
    return ans;
}

