  rep(i, a.size()){
    ans[i] = b[a[i]];
  }
  return ans;
}

vi pow(const vi& a, ll k) {
  if (k == 0) {
    vi ans(a.size());
    rep(i, a.size()) ans[i] = i;
    return ans;
  }

  vi ans = pow(a, k / 2);
  ans = multiply(ans, ans);
  if (k & 1LL) {
    ans = multiply(ans, a);
  }
  return ans;
}

int main(){
  ll n, k;
  cin >> n >> k;
  vi p(n), q(n);
  rep(i,n) {
    cin >> p[i]; p[i]--;
  }
  rep(i,n) {
    cin >> q[i]; q[i]--;
  }

  vi ans(n);
  if (k < 30) {
    vi a1 = p, a2 = q;
    for(int i = 3; i <= k; ++i) {
      vi a3 = multiply(inverse(a1), a2);
      swap(a1, a2);
      swap(a2, a3);
    }
    ans = a2;
  } else {
    vi prefix_base = multiply(inverse(q), multiply(p, multiply(q, inverse(p))));
    vi prefix = pow(prefix_base, (k-2) / 6);
    vi suffix_base = multiply(inverse(p), multiply(q, multiply(p, inverse(q))));
    vi suffix = pow(suffix_base, (k-2) / 6);
    vi very_suffix = multiply(inverse(p), q);

    vi core = multiply(inverse(q), multiply(p, multiply(q, multiply(p, inverse(q)))));
    if (k % 6 == 2LL) {
      core = p;
