      core.resize(n);
      rep(i, n) core[i] = i;
    } else if (k % 6 == 4LL) {
      core = inverse(q);
    } else if (k % 6 == 5LL) {
      core = multiply(inverse(q), multiply(p, inverse(q)));
    } else if (k % 6 == 0LL) {
      core = multiply(inverse(q), multiply(p, multiply(p, inverse(q))));
    }

    ans = multiply(prefix, multiply(core, multiply(suffix, very_suffix)));
  }

  rep(i, n) {
    cout << ans[i] + 1 << " \n"[i == n-1];
  }
}

