vi rev_map(const vi &f) {
	int n = f.size();
	vi ret(n);
	Loop(i, n) {
		ret[f[i]] = i;
	}
	return ret;
}

// g, then, f
vi seq_map(const vi &f, const vi &g) {
	int n = f.size();
	vi ret(n);
	Loop(i, n) {
		ret[i] = f[g[i]];
	}
	return ret;
}

vi pow_map(const vi &f, int p) {
	vi ret;
	if (p == 0) {
		ret.resize(f.size());
		Loop(i, f.size()) ret[i] = i;
	}
	else if (p == 1) ret = f;
	else {
		ret = pow_map(f, p / 2);
		ret = seq_map(ret, ret);
		if (p % 2 == 1) ret = seq_map(f, ret);
	}
	return ret;
}

