} 

vector <int> Pow(vector <int> x, int y) {
	vector <int> res; res.clear();
	for(int i = 0; i < n; ++ i) res.push_back(i);
	for(; y; y >>= 1, x = x * x) {
		if(y & 1)
		res = res * x;
	}
	return res;
}

