    return res;
  };
  auto pow = [&](V<> p, int k) -> V<> {
    V<> res(n);
    iota(begin(res), end(res), 0);
    while (k) {
      if (k & 1) res = cp(res, p);
      p = cp(p, p);
      k >>= 1;
