vector<int> ID;

vector<int> po(vector<int> &X, int k) {
	if (k == 0) {
		return ID;
	}
	vector<int> res = po(X, k / 2);
	res = mul(res, res);
	if (k % 2 == 1) {
		res = mul(res, X);
	}
	return res;
}

