// P,Qp,qP[i][j] == 1 <==> p[i] = j 
// N0N+1
std::vector<int> perm_product(std::vector<int> p, std::vector<int> q) {
    assert(p.size() == q.size());
    std::vector<int> r;
    r.resize(p.size());
    int N = p.size() - 1;
    for (int i = 1; i <= N; i++) {
        r[i] = q[p[i]];
    }
    return r;
}

// Pp
std::vector<int> perm_trans(std::vector<int> p) {
    std::vector<int> r;
    r.resize(p.size());
    int N = p.size() - 1;
    for (int i = 1; i <= N; i++) {
        r[p[i]] = i;
    }
    return r;
}

// Pp
std::vector<int> perm_pow(std::vector<int> p, long long K) {
    std::vector<int> r;
    r.resize(p.size());
    int N = p.size() - 1;
    for (int i = 1; i <= N; i++) {
        r[i] = i;
    }

    while (K > 0) {
        if (K & 1) {
            r = perm_product(r, p);
        }
        p = perm_product(p, p);
        K = (K >> 1);
    }

    return r;
}

