int n, k;

struct Perm {
    int* vals;
};

Perm operator*(Perm a, Perm b)
{
    Perm ans;
    ans.vals = new int[n + 5];
    for (int i = 1; i <= n; i++) {
        ans.vals[i] = b.vals[a.vals[i]];
    }
    return ans;
}

Perm operator^(Perm a, int pwr)
{
    Perm ans;
    ans.vals = new int[n + 5];
    bool* vis = new bool[n + 5];
    for (int i = 1; i <= n; i++) vis[i] = false;
    for (int i = 1; i <= n; i++) {
        if (vis[i]) continue;
        vector<int> cycle;
        int cur = i;
        do {
            vis[cur] = true; cycle.push_back(cur); cur = a.vals[cur];
        } while (cur != i);
        for (int x = 0; x < cycle.size(); x++) {
            ans.vals[cycle[x]] = cycle[(x + pwr) % cycle.size()];
        }
    }
    delete[] vis;
    return ans;
}

Perm inv(Perm a)
{
    Perm ans;
    ans.vals = new int[n + 5];
    for (int i = 1; i <= n; i++) {
        ans.vals[a.vals[i]] = i;
    }
    return ans;
}

