int main()
{
    scanf("%d%d", &n, &k);
    Perm p, q;
    p.vals = new int[n + 5];
    q.vals = new int[n + 5];
    for (int i = 1; i <= n; i++) scanf("%d", p.vals + i);
    for (int i = 1; i <= n; i++) scanf("%d", q.vals + i);
    Perm ans;
    if (k < 6) {
        a[1] = p; a[2] = q;
        for (int i = 3; i <= k; i++) {
            a[i] = inv(a[i-2]) * a[i-1];
        }
        ans = a[k];
    } else {
        Perm perm_x = p * q * inv(p) * inv(q);
        Perm perm_y = q * p * inv(q) * inv(p);
        int cnt = (k % 6 >= 4 ? k / 6 : (k / 6) - 1);
        Perm middle;
        if (k % 6 == 0) {
            middle = p * p * inv(q) * inv(p);
        } else if (k % 6 == 1) {
            middle = p * q * p * inv(q) * inv(p);
        } else if (k % 6 == 2) {
            middle = p * q * inv(p) * q * p * inv(q) * inv(p);
        } else if (k % 6 == 3) {
            middle = p * q * inv(p) * inv(p) * q * p * inv(q) * inv(p);
        } else if (k % 6 == 4) {
            middle = inv(p);
        } else {
            middle = p * inv(q) * inv(p);
        }
        ans = inv(q) * (perm_x ^ cnt) * middle * (perm_y ^ cnt) * q;
    }
    for (int i = 1; i <= n; i++) {
        printf("%d ", ans.vals[i]);
    }
    return 0;
}

