namespace ProjectPermy
{
	struct perm
	{
		vector<int>v;
		int &operator[](int x){return v[x];}
		const int &operator[](int x)const{return v[x];}
		int size()const{return v.size()-1;}
		void resize(int n){v.resize(n+1);}
		void read(int n){v.resize(n+1);for(int i=1;i<=size();i++)scanf("%d",&v[i]);}
		void out()
		{
			for(int i=1;i<=size();i++)
				printf("%d ",v[i]);
			printf("\n");
		}
	};
	perm e(int n)
	{
		perm ans;
		ans.resize(n);
		for(int i=1;i<=n;i++)
			ans[i]=i;
		return ans;
	}
	perm operator*(perm a,perm b)
	{
		perm ans=e(a.size());
		perm rev=e(a.size());
		for(int i=1;i<=a.size();i++)
			ans[i]=a[b[i]];
		return ans;
	}
	perm operator~(perm a)
	{
		perm ans=e(a.size());
		for(int i=1;i<=a.size();i++)
			ans[a[i]]=i;
		return ans;
	}
	vector<int>vis,rem;
	perm operate;
	void find_cycle(int x)
	{
		if(vis[x]) return;
		vis[x]=1;
		rem.push_back(x);
		find_cycle(operate[x]);
	}
	perm Pow(perm a,int x)
	{
		perm ans=e(a.size());
		vector<vector<int> >cycle;
		vis.clear();
		vis.resize(a.size()+1);
		operate=a;
		for(int i=1;i<=a.size();i++)
		{
			if(!vis[i])
			{
				rem.clear();
				find_cycle(i);
				cycle.push_back(rem);
			}
		}
		for(int i=0;i<cycle.size();i++)
		{
			for(int j=0;j<cycle[i].size();j++)
				ans[cycle[i][j]]=cycle[i][(j+x)%cycle[i].size()];
		}
		return ans;
	}
}
