vector<ll> mat_mul2(int n, vector<ll>& a, vector<ll>& b) {
  vector<ll> ret(n+1);
  for(int i=1; i<=n; i++) {
    ret[i] = a[b[i]];
  }
  return ret;
}

vector<ll> mat_pow2(vector<ll> mat, int n, int size) {
  vector<ll> res(size+1);
  vector<ll> a = mat;
  for(int i=1; i<=size; i++) {
    res[i] = i;
  }
  while(n > 0) {
    if (n&1) {
      res = mat_mul2(size, res, a);
    }
    a = mat_mul2(size, a, a); n >>= 1;
  }
  return res;
}

vector<ll> mat_inv(vector<ll>& a, int n) {
  vector<ll> ret(n+1);
  for(int i=1; i<=n; i++) {
    ret[a[i]] = i;
  }
  return ret;
}

