	go(idx, 'q');
	go(idx, 'P');

	REP (i, N) cur[i] = i;
	go(cur, 'Q');
	go(cur, 'p');
	go(cur, 'q');
	go(cur, 'P');

	for (int k=r; k; k>>=1) {
	    if (k & 1) {
		REP (i, N) buf[i] = idx[cur[i]];
		REP (i, N) idx[i] = buf[i];
	    }
	    REP (i, N) buf[i] = cur[cur[i]];
	    REP (i, N) cur[i] = buf[i];
	}

	string z;
