	    go(idx, "QpqP"[i]);
	}
	if (m % 2) {
	    go(idx, "QpqP"[m/2]);
	}
	reverse(z.begin(), z.end());
	REP (i, m/2) {
	    go(idx, z[i]);
	}

	REP (i, N) cur[i] = i;
	go(cur, 'P');
	go(cur, 'q');
	go(cur, 'p');
	go(cur, 'Q');

	for (int k=r; k; k>>=1) {
	    if (k & 1) {
		REP (i, N) buf[i] = idx[cur[i]];
		REP (i, N) idx[i] = buf[i];
	    }
	    REP (i, N) buf[i] = cur[cur[i]];
	    REP (i, N) cur[i] = buf[i];
	}
    }

    REP (i, N) idx[i]++;
    rprintf("%d", idx, idx+N);
}

