  for (char c : s) {
    if (c == 'p') ret = cmp(ret, p);
    else if (c == 'q') ret = cmp(ret, q);
    else if (c == 'P') ret = cmp(ret, inv(p));
    else if (c == 'Q') ret = cmp(ret, inv(q));
  }
