typedef long long int ll;
typedef pair<int, int> P;
int n;
void pow(int p[100001], int k, int ret[100001]){
    int pp[31][100001];
    for(int i=1; i<=n; i++){
        pp[0][i]=p[i];
    }
    for(int j=1; (1<<j)<=k; j++){
        for(int i=1; i<=n; i++){
            pp[j][i]=pp[j-1][pp[j-1][i]];
        }
    }
    for(int i=1; i<=n; i++) ret[i]=i;
    for(int j=0; (1<<j)<=k; j++){
        if(k&(1<<j)){
            int ret2[100001];
            for(int i=1; i<=n; i++){
                ret2[i]=ret[pp[j][i]];
            }
            for(int i=1; i<=n; i++) ret[i]=ret2[i];
        }
    }
}
void mul(int p[100001], int q[100001], int ret[100001]){
    for(int i=1; i<=n; i++){
        ret[i]=p[q[i]];
    }
}
int main()
{
    int k; cin>>n>>k;
    int p[100001], q[100001], pi[100001], qi[100001];
    for(int i=1; i<=n; i++){
        cin>>p[i]; pi[p[i]]=i;
    }
    for(int i=1; i<=n; i++){
        cin>>q[i]; qi[q[i]]=i;
    }
    int r[100001], rp[100001], r1[100001], r2[100001];
    mul(q, pi, r1);
    mul(qi, p, r2);
    mul(r1, r2, r);
    pow(r, (k-1)/6, rp);
    if((k-1)%6>=3){
        int rp2[100001];
        mul(rp, r1, rp2);
        for(int i=1; i<=n; i++) rp[i]=rp2[i];
    }
    int rpi[100001];
    for(int i=1; i<=n; i++){
        rpi[rp[i]]=i;
    }
    int s[100001];
    if((k-1)%6==0){
        for(int i=1; i<=n; i++) s[i]=p[i];
    }else if((k-1)%6==1){
        for(int i=1; i<=n; i++) s[i]=q[i];
    }else if((k-1)%6==2){
        for(int i=1; i<=n; i++) s[i]=r1[i];
    }else if((k-1)%6==3){
        for(int i=1; i<=n; i++) s[i]=pi[i];
    }else if((k-1)%6==4){
        for(int i=1; i<=n; i++) s[i]=qi[i];
    }else{
        for(int i=1; i<=n; i++) s[i]=r2[i];
    }
    int ans2[100001];
    mul(rp, s, ans2);
    int ans[100001];
    mul(ans2, rpi, ans);
    for(int i=1; i<=n; i++){
        if(i<n) cout<<ans[i]<<" ";
        else cout<<ans[i]<<endl;
    }
    return 0;
}

