vector<int> getInv(vector<int>& v) {
    vector<int> res(v.size());
    for (int i = 0; i < v.size(); i++) {
        res[v[i]] = i;
    }
    return res;
}

vector<int> getMul(vector<int> p, vector<int> q) {
    vector<int> res(p.size());
    for (int i = 0; i < p.size(); i++) {
        res[i] = q[p[i]];
    }
    return res;
}

vector<int> getNext(vector<int>& p, vector<int>& q) {
    vector<int> res(p.size());
    for (int i = 0; i < p.size(); i++) {
        res[p[i]] = q[i];
    }
    return res;
}

vector<int> getPow(vector<int> v, int n) {
    vector<int> res(v.size());
    for (int i = 0; i < v.size(); i++) res[i] = i;
    while (n > 0) {
        if (n & 1) res = getMul(res, v);
        v = getMul(v, v);
        n >>= 1;
    }
    return res;
}

int main() {
    cin.tie(0);
    ios::sync_with_stdio(false);
    int n, K;
    cin >> n >> K;
    vector<int> p(n), q(n);
    for (int i = 0; i < n; i++) {
        cin >> p[i];
        p[i]--;
    }

    for (int i = 0; i < n; i++) {
        cin >> q[i];
        q[i]--;
    }

    vector<int> pinv = getInv(p), qinv = getInv(q);
    vector<int> v1 = getPow(getMul(getMul(getMul(p, qinv), pinv), q), (K - 1) / 6);
    vector<int> v2 = getPow(getMul(getMul(getMul(qinv, p), q), pinv), (K - 1) / 6);
    vector<int> v3;
    if ((K - 1) % 6 == 0) v3 = p;
    else if ((K - 1) % 6 == 1) v3 = q;
    else {
        for (int i = 2; i <= (K - 1) % 6; i++) {
            v3 = getNext(p, q);
            p = q;
            q = v3;
        }
    }
    vector<int> ans = getMul(getMul(v2, v3), v1);

    for (int i = 0; i < n; i++) {
        cout << ans[i] + 1 << " \n"[i + 1 == n];
    }
    return 0;
}

