vector<int> compose(vector<int> a, vector<int> b) {
	int n = a.size();
	vector<int> c(n);
	for (int i = 0; i < n; ++i) c[i] = a[b[i]];
	return c;
}
vector<int> power(vector<int> a, int b) {
  int n = a.size();
  vector<int> x(n);
  iota(x.begin(), x.end(), 0);
  while (b) {
    if (b & 1) x = compose(x, a);
    a = compose(a, a);
    b >>= 1;
  }
  return x;
}
