vector<int> inv(vector<int> a) {
  int n = (int) a.size();
  vector<int> ret(n);
  for (int i = 0; i < n; i++) {
    ret[a[i]] = i;
  }
  return ret;
}

vector<int> mul(vector<int> a, vector<int> b) {
  int n = (int) a.size();
  vector<int> ret(n);
  for (int i = 0; i < n; i++) {
    ret[i] = a[b[i]];
  }
  return ret;
}

vector<int> power(vector<int> a, int b) {
  int n = (int) a.size();
  vector<int> ret(n);
  iota(ret.begin(), ret.end(), 0);
  while (b) {
    if (b & 1) {
      ret = mul(ret, a);
    }
    b >>= 1;
    a = mul(a, a);
  }
  return ret;
}

void output(vector<int> a) {
  for (int i = 0; i < (int) a.size(); i++) {
    if (i) {
      cout << " ";
    }
    cout << a[i] + 1;
  }
  cout << '\n';
  exit(0);
}

int main() {
  int n, k;
  cin >> n >> k;
  vector<int> a(n), b(n);
  for (int i = 0; i < n; i++) {
    cin >> a[i];
    a[i]--;
  }
  for (int i = 0; i < n; i++) {
    cin >> b[i];
    b[i]--;
  }
  if (k == 1) {
    output(a);
  }
  vector<int> A = inv(a);
  vector<int> B = inv(b);
  vector<int> bABa = mul(b, mul(A, mul(B, a)));
  vector<int> AbaB = mul(A, mul(b, mul(a, B)));

  vector<int> l[6];
  l[0] = b;
  l[1] = mul(b, A);
  l[2] = mul(b, mul(A, B));
  l[3] = mul(bABa, B);
  l[4] = mul(bABa, mul(a, B));
  l[5] = mul(bABa, mul(b, mul(a, B)));

  vector<int> f = power(AbaB, (k - 2) / 6);
  f = mul(l[(k - 2) % 6], f);
  f = mul(power(bABa, (k - 2) / 6), f);
  output(f);
  return 0;
}

