vector<int> perm_mult(const vector<int>& a, const vector<int>& b) {
    vector<int> res(SZ(a), -1);

    for (int i = 0; i != SZ(a); ++i)
        res[i] = a[b[i]];

    return res;
}

vector<int> perm_inv(const vector<int>& a) {
    vector<int> res(SZ(a), -1);

    for (int i = 0; i != SZ(a); ++i)
        res[a[i]] = i;

    return res;
}

vector<int> perm_pow(vector<int> a, int k) {
    vector<int> res(SZ(a));
    std::iota(ALL(res), 0);

    while (k) {
        if (k % 2) {
            vector<int> resc = perm_mult(res, a);
            res = resc;
        }

        vector<int> a_c = perm_mult(a, a);
        a = a_c;
        k /= 2;
    }

    return res;
}

