vector<int> pow_(vector<int> x, int n){
    vector<int> ans(x.size()), xx = x;
    iota(ans.begin(),ans.end(), 0);
    while(n > 0){
        if(n & 1) ans = mul(ans, xx);
        xx = mul(xx, xx);
        n >>= 1;
    }
    return ans;
}

