V<int> inv(V<int> a) {
	V<int> res(a.size());
	REP(i, a.size()) res[a[i]] = i;
	return res;
}

V<int> merge(V<int> a, V<int> b) {
	V<int> res(a.size());
	REP(i, a.size()) res[i] = a[b[i]];
	return res;
}

V<int> id(int n) {
	V<int> res(n);
	std::iota(ALL(res), 0);
	return res;
}

V<int> repeating(V<int> a, int p) {
	V<int> res = id(a.size());
	while (p) {
		if (p & 1) res = merge(res, a);
		a = merge(a, a);
		p >>= 1;
	}
	return res;
}

