vector<int> mult(vector<int> a, vector<int> b) {
	// permutation a * b
	int N = a.size();
	vector<int> ret(N);
	for(int i = 0; i < N; i++) ret[i] = a[b[i]];
	return ret;
}

vector<int> inv(vector<int> a) {
	// permutation a^-1
	int N = a.size();
	vector<int> ret(N);
	for(int i = 0; i < N; i++) ret[a[i]] = i;
	return ret;
}

vector<int> pw(vector<int> & a, int e) {
	int N = a.size();
	if(e <= 0) {
		vector<int> id(N);
		for(int i = 0; i < N; i++) id[i] = i;
		return id;
	}
	vector<int> x = pw(a, e/2);
	x = mult(x, x);
	if(e&1) x = mult(x, a);
	return x;
}

vector<int> lift(vector<int> p, vector<int> q, int K) {
	if(K == 0) return mult(inv(q), p);
	vector<int> b = mult(q, mult(inv(p), mult(inv(q), p)));
	vector<int> b_pw = pw(b, K);
	return mult(b_pw, mult(lift(p, q, 0), inv(b_pw)));
}

void print(vector<int> p) {
	int N = p.size();
	for(int i = 0; i < N; i++) cout << p[i]+1 << ((i+1 == N) ? "\n" : " ");
}

