		P[2][i]--;
	}
	P[0] = mult(inv(P[2]), P[1]);
	for(int i = 3; i < 6; i++) P[i] = mult(P[i-1], inv(P[i-2]));
	vector<int> b = mult(P[2], mult(inv(P[1]), mult(inv(P[2]), P[1])));
	vector<int> b_pw = pw(b, K/6);
	print(mult(b_pw, mult(P[K%6], inv(b_pw))));
	return 0;
}

