ll N, K;

vector<int> cross(vector<int> a, vector<int> b) {
    vector<int> ret(N+1);
    ret[0] = -1;
    for(int i = 1; i <= N; i++) {
        ret[i] = a[b[i]];
    }
    return ret;
}

vector<int> inverse(vector<int> a) {
    vector<int> ret(N+1);
    ret[0] = -1;
    for(int i = 1; i <= N; i++) {
        ret[a[i]] = i;
    }
    return ret;
}

vector<int> beki(vector<int> a, int b) {
    vector<int> ret(N+1);
    ret[0] = -1;
    if(b == 0) {
        for(int i = 1; i <= N; i++) ret[i] = i;
        return ret;
    }
    ret = beki(a, b / 2);
    ret = cross(ret, ret);
    if(b % 2 == 1) ret = cross(ret, a);
    return ret;
}

int main() {
    //cout.precision(10);
    cin.tie(0);
    ios::sync_with_stdio(false);
    cin >> N >> K;
    vector<int> p(N+1);
    vector<int> q(N+1);
    for(int i = 1; i <= N; i++) cin >> p[i];
    for(int i = 1; i <= N; i++) cin >> q[i];
    vector<int> pInv = inverse(p);
    vector<int> qInv = inverse(q);
    vector<int> initial[7];
    initial[1] = p;
    initial[2] = q;
    initial[3] = cross(q, pInv);
    initial[4] = cross(q, cross(pInv, qInv));
    initial[5] = cross(q, cross(pInv, cross(qInv, cross(p, qInv))));
    initial[6] = cross(q, cross(pInv, cross(qInv, cross(p, cross(p, qInv)))));
    vector<int> S = cross(q, cross(pInv, cross(qInv, p)));
    int ans_index = (K - 1) % 6 + 1;
    int ans_times = (K - ans_index) / 6;
    S = beki(S, ans_times);
    vector<int> ans = cross(S, cross(initial[ans_index], inverse(S)));
    for(int i = 1; i <= N; i++) cout << ans[i] << " ";
    cout << endl;
    return 0;
}

