    Rep(i, N) if (!vis[i]) {
        top = 0;
        for (int j = i; !vis[j]; j = a[j]) {
            vis[j] = true;
            st[++top] = j;
        }
        Rep(i, top) pos[st[i]] = i;
        int z = y % top;
        for (int j = i; ; j = a[j]) {
            ans[j] = st[(pos[a[j]] + z - 1) % top + 1];
            if (j == st[top]) break;
        }
        // Rep(i, top) {
