	for (int i = 0; i < N; i++) r[i] = a[b[i]];
	return r;
}

perm pow(perm a, int b) {
	perm r(N);
	iota(r.begin(),r.end(),0);
	while (b) {
		if (b&1) r = r * a;
		a = a * a;
		b >>= 1;
	}
	return r;
}

