	switch(K % 6) {
		case 0:
			mid = P;
			break;
		case 1:
			mid = Q;
			break;
		case 2:
			mid = Q * inv(P);
			break;
		case 3:
			mid = Q * inv(P) * inv(Q);
			break;
		case 4:
			mid = Q * inv(P) * inv(Q) * P * inv(Q);
			break;
		case 5:
			mid = Q * inv(P) * inv(Q) * P * P * inv(Q);
			break;
		default:
			assert(false);
	}
	perm res = pref * mid * inv(pref);

	for (int i = 0; i < N; i++) cout << res[i]+1 << " \n"[i+1==N];

	return 0;
}

