vector<int> rev(vector<int> &a){
    int n = a.size();
    vector<int> res(n);
    for (int i=0;i<n;i++) res[a[i]] = i;
    return res;
}
vector<int> mult(vector<int> a, vector<int> b){
    int n = a.size();
    vector<int> res(n);
    for (int i=0; i < n; i++) res[i] = b[a[i]];
    return res;
}
vector<int> pw(vector<int> &a, int d){
    if (d==0){
        vector<int> K;
        for (int i=0;i<a.size();i++) K.push_back(i);
        return K;
    }
    if (d==1) return a;
    if (d % 2 == 0){
        vector<int> res = pw(a,d/2);
        return mult(res, res);
    }
    return mult(a, pw(a,d-1));
}
vector<int> get(vector<vector<int> > kek, int d){
    int n = kek[0].size();
    vector<int> cp;
    for (int i=0;i<n;i++) cp.push_back(i);
    for (int i=0;i<4;i++) cp = mult(cp, kek[i]);
    int N = d/4;
    cp = pw(cp, N);
    for (int i=0;i<(d%4);i++) cp = mult(cp, kek[i]);
    return cp;
}
