  vi inv(perm.size());
  rep(i, perm.size()) {
    inv[perm[i]] = i;
  }
  return inv;
}

vi multiply(const vi& a, const vi& b) {
  vi ans(a.size());
  rep(i, a.size()){
    ans[i] = b[a[i]];
  }
  return ans;
}

vi pow(const vi& a, ll k) {
  if (k == 0) {
    vi ans(a.size());
    rep(i, a.size()) ans[i] = i;
    return ans;
  }

  vi ans = pow(a, k / 2);
  ans = multiply(ans, ans);
  if (k & 1LL) {
    ans = multiply(ans, a);
  }
  return ans;
}

