using X = vector<int>;

X inverse(X x) {
  X r(x.size());
  for(int i = 0; i < x.size(); i++) r[x[i]] = i;
  return r;
}

X op(X a, X b) {
  X r(a.size());
  for(int i = 0; i < a.size(); i++) r[i] = a[b[i]];
  return r;
}

X f(X a, X b) {
  X r(a.size());
  for(int i = 0; i < a.size(); i++) r[a[i]] = b[i];
  return r;
}

int n, k;

X getE() {
  X r(n);
  iota(begin(r), end(r), 0);
  return r;
}

X x[4];
int cycle[] = {1, 3, 2, 0};

// 

X get(int start, int t) {
  int t4 = t / 4;
  auto a = getE();
  for(int i = 0; i < 4; i++) a = op(a, x[cycle[(start + i) % 4]]);
  auto r = getE();
  while(t4) {
    if(t4 & 1) r = op(r, a);
    a = op(a, a);
    t4 >>= 1;
  }
  t %= 4;
  for(int i = 0; i < t; i++) r = op(r, x[cycle[(start + i) % 4]]);
  return r;
}


int main() {
  std::ios::sync_with_stdio(false), std::cin.tie(0);

  cin >> n >> k;
  k--;
  X p(n), q(n);
  for(auto & e: p) cin >> e, e--;
  for(auto & e: q) cin >> e, e--;
  x[0] = p;
  x[1] = q;
  x[2] = inverse(q);
  x[3] = inverse(p);

  // assert(p == inverse(x[3]));
  // assert(q == inverse(x[2]));

  vector<vector<int>> v;
  v.push_back({0});
  v.push_back({1});
  for(int i = 0; i < 15; i++) {
    auto u = v[i];
    auto w = v[i+1];
    for(auto &e : u) e = 3 - e;
    while(u.size() && w.size() && u.back() + w.back() == 3) w.pop_back(), u.pop_back();
    reverse(u.begin(), u.end());
    w.insert(w.end(), u.begin(), u.end());
    v.push_back(w);
    // dump(i, v[i].size());
    // dump(v[i]);
  }

  auto r = getE();
  if(k <= 10) {
    for(int i = 0; i < v[k].size(); i++) r = op(r, x[v[k][i]]);
  } else {
    int c = 5 + 2 * ((k - 6) / 3);
    int len = 6 + 4 * ((k - 5) / 3);
    switch(k % 3) {
      case 0:
        len += 1;
        break;
      case 1:
        len += 3;
        break;
      case 2:
        c++;
        break;
    }
    assert(len >= c);
    auto a = get(0, c);
    auto b = inverse(get(0, len - c));
    r = op(a, b);
  }

  for(int i = 0; i < n; i++) cout << r[i] + 1 << " \n"[i == n - 1];

  return 0;
}

