vector<int> inv(vector<int> a) {
  int n = (int) a.size();
  vector<int> b(n);
  for (int i = 0; i < n; i++) b[a[i]] = i;
  return b;
}

void output(vector<int> a) {
  int n = (int) a.size();
  for (int i = 0; i < n; i++) {
    if (i > 0) cout << " ";
    cout << a[i] + 1;
  }
  cout << '\n';
  exit(0);
}

vector<int> apply(vector<int> a, vector<int> b) {
  int n = (int) a.size();
  vector<int> c(n);
  for (int i = 0; i < n; i++) {
    c[i] = a[b[i]];
  }
  return c;
}

vector<int> power(vector<int> a, int b) {
  int n = (int) a.size();
  vector<int> x(n);
  iota(x.begin(), x.end(), 0);
  while (b > 0) {
    if (b & 1) {
      x = apply(x, a);
    }
    a = apply(a, a);
    b >>= 1;
  }
  return x;
}

int main() {
  ios::sync_with_stdio(false);
  cin.tie(0);
  int n, k;
  cin >> n >> k;
  vector<int> a(n);
  for (int i = 0; i < n; i++) {
    cin >> a[i];
    a[i]--;
  }
  vector<int> b(n);
  for (int i = 0; i < n; i++) {
    cin >> b[i];
    b[i]--;
  }
  vector<int> A = inv(a);
  vector<int> B = inv(b);
  if (k == 1) {
    output(a);
  }
  if (k == 2) {
    output(b);
  }
  int pref = (k * 2 - 4) / 3;
  vector<int> p = power(apply(b, apply(A, apply(B, a))), pref / 4);
  if (pref % 4 == 3) p = apply(p, apply(b, apply(A, B)));
  if (pref % 4 == 2) p = apply(p, apply(b, A));
  if (pref % 4 == 1) p = apply(p, b);
  vector<int> P = inv(p);
  vector<int> res = P;
  if (k % 6 == 1) res = apply(a, res);
  if (k % 6 == 2) res = apply(b, res);
  if (k % 6 == 3) res = apply(b, apply(A, res));
  if (k % 6 == 4) res = apply(A, res);
  if (k % 6 == 5) res = apply(B, res);
  if (k % 6 == 0) res = apply(B, apply(a, res));
  res = apply(p, res);
  output(res);
  return 0;
}

