    h = Conv(h, rp);
    ans = Conv(Conv(h, Conv(rq, p)), Rev(h));
    break;
  case 3:
    h = Conv(h, Conv(rp, rq));
    ans = Conv(Conv(h, p), Rev(h));
    break;
  case 4:
    h = Conv(h, Conv(Conv(rp, rq), p));
    ans = Conv(Conv(h, q), Rev(h));
    break;
  case 5:
    h = Conv(h, Conv(Conv(rp, rq), p));
    ans = Conv(Conv(h, Conv(q, rp)), Rev(h));
    break;
  default:
    assert(0);
  }
  Out(ans);
}

