typedef long long ll;

ll n, k;
vector<ll> p, q, ip, iq, tot1, tot2, id;

vector<ll> mul(vector<ll> a, vector<ll> b) {
	vector<ll> ans;
	ans.resize(n);
	for (ll i = 0; i < n; ++i)
		ans[i] = a[b[i]];
	return ans;
}

vector<ll> inv(vector<ll> a) {
	vector<ll> ans;
	ans.resize(n);
	for (ll i = 0; i < n; ++i)
		ans[a[i]] = i;
	return ans;
}

vector<ll> exp(vector<ll> a, ll j) {
	if (j == 0)
		return id;
	if (j%2 == 1)
		return mul(a, exp(a, j-1));
	auto b = exp(a, j/2);
	return mul(b, b);
}

vector<ll> get1(ll j) {
	ll l = 2*((j-1)/3);
	if (j%3 != 1)
		l += 2;
	auto ans = exp(tot1, l/4);
	if (l%4 == 0)
		return ans;
	if (l%4 == 1)
		return mul(ans, q);
	if (l%4 == 2)
		return mul(ans, mul(q, ip));
	if (l%4 == 3)
		return mul(ans, mul(q, mul(ip, iq)));
}

vector<ll> get2(ll j) {
	ll l = 2*((j-1)/3);
	if (j%3 != 0)
		l--;
	auto ans = exp(tot2, l/4);
	if (l%4 == 0)
		return ans;
	if (l%4 == 1)
		return mul(iq, ans);
	if (l%4 == 2)
		return mul(p, mul(iq, ans));
	if (l%4 == 3)
		return mul(q, mul(p, mul(iq, ans)));
}

