		cin >> q[i]; q[i]--;
	}
	id.resize(n);
	for (ll i = 0; i < n; ++i)
		id[i] = i;

	ip = inv(p);
	iq = inv(q);
	tot1 = mul(q, mul(ip, mul(iq, p)));
	tot2 = mul(ip, mul(q, mul(p, iq)));

	vector<ll> ans;
	if (k == 1)
		ans = p;
	else if (k == 2)
		ans = q;
	else if (k == 3)
		ans = mul(q, ip);
	else
		ans = mul(get1(k), get2(k));
	for (ll i = 0; i < n; ++i)
		cout << ans[i]+1 << ' ';
	cout << endl;
}

