    ans = q;
  } else if (k < 30) {
    vi a1 = p, a2 = q;
    for(int i = 3; i <= k; ++i) {
      vi a3 = multiply(inverse(a1), a2);
      swap(a1, a2);
      swap(a2, a3);
    }
    ans = a2;
  } else {
    vi prefix_base = multiply(inverse(q), multiply(p, multiply(q, inverse(p))));
    vi prefix = pow(prefix_base, (k-2) / 6);
    vi suffix_base = multiply(inverse(p), multiply(q, multiply(p, inverse(q))));
    vi suffix = pow(suffix_base, (k-2) / 6);
    vi very_suffix = multiply(inverse(p), q);

    vi core = multiply(inverse(q), multiply(p, multiply(q, multiply(p, inverse(q)))));
    if (k % 6 == 2LL) {
      core = p;
    } else if (k % 6 == 3LL) {
      core.resize(n);
      rep(i, n) core[i] = i;
    } else if (k % 6 == 4LL) {
      core = inverse(q);
    } else if (k % 6 == 5LL) {
      core = multiply(inverse(q), multiply(p, inverse(q)));
    } else if (k % 6 == 0LL) {
      core = multiply(inverse(q), multiply(p, multiply(p, inverse(q))));
    }

    ans = multiply(prefix, multiply(core, multiply(suffix, very_suffix)));
  }

  rep(i, n) {
    cout << ans[i] + 1 << " \n"[i == n-1];
  }
}

