vector<int> inverse(const vector<int> &a) {
    vector<int> b(a.size());
    for (int i = 0; i < a.size(); ++i)
        b[a[i]] = i;
    return b;
}
vector<int> power(vector<int> a, int k) {
    vector<bool> visited(a.size());
    for (int i = 0; i < a.size(); ++i) {
        if (visited[i])
            continue;
        vector<int> b;
        for (int j = i; !visited[j]; visited[j] = true, j = a[j])
            b.push_back(j);
        for (int j = 0; j < b.size(); ++j)
            a[b[j]] = b[(j + k) % b.size()];
    }
    return a;
}
