vector<int> binPow(vector<int> a, int m) {
    int n = sz(a);
    vector<int> res = id(n);
    for (; m > 0; m >>= 1) {
        if (m & 1) {
            res = res * a;
        }
        a = a * a;
    }
    return res;
}

