vector<int> id(int n) {
    vector<int> res(n);
    for (int i = 0; i < n; ++i) {
        res[i] = i;
    }
    return res;
}

vector<int> binPow(vector<int> a, int m) {
    int n = sz(a);
    vector<int> res = id(n);
    for (; m > 0; m >>= 1) {
        if (m & 1) {
            res = res * a;
        }
        a = a * a;
    }
    return res;
}

vector<int> rev(const vector<int> &a) {
    int n = sz(a);
    vector<int> res(n);
    for (int i = 0; i < n; ++i) {
        res[a[i]] = i;
    }
    return res;
}

