  	Per b(n);
  	for(int i=1;i<=n;i++) b.num[num[i]]=i;
  	return b;
  }
  Per operator * (Per b) {
  	Per c(n);
  	for(int i=1;i<=n;i++) c.num[i]=num[b.num[i]];
  	return c;
  }
  Per operator ^ (int k) {
