typedef vector<int> ve;
int n,k;
ve p,q,A,B,ip,iq;
void re(ve &x) {
	x.resize(n+1);
	fill(x.begin(),x.begin()+1+n,0);
}
ve inv(ve a) {
	ve orz;
	re(orz);
	for(int i=1;i<=n;i++) {
		orz[a[i]] = i;
	}
	return orz;
}
ve operator*(ve aa,ve bb) {
	ve orz; re(orz);
	for(int i=1;i<=n;i++) {
		orz[i] = aa[bb[i]];
	}
	return orz;
}
ve ksm(ve aa,int b) {
	ve orz; re(orz);
	for(int i=1;i<=n;i++) orz[i] = i;
	for(;b;b>>=1,aa = aa*aa) {
		if(b&1) {
			orz = orz*aa;
		}
	}
	return orz;
}
int main() {
	scanf("%d%d",&n,&k);
	re(p); re(q);
	for(int i=1;i<=n;i++) scanf("%d",&p[i]);
	for(int i=1;i<=n;i++) scanf("%d",&q[i]);
	ip = inv(p); iq = inv(q);
	ve yooo = q*ip*iq*p;
	A = ksm(yooo,(k-1)/6);
	int yo = k - (k-1)/6*6;
	switch(yo) {
		case 1:{
			B = p;
			break;
		}
		case 2:{
			B = q;
			break;
		}
		case 3:{
			B = q * ip;
			break;
		}
		case 4:{
			B = ip;
			A = A*q;
			break;
		}
		case 5:{
			B = iq;
			A = A*q*ip;
			break;
		}
		case 6:{
			B = iq*p;
			A = q*ip;
			break;
		}
	}
	ve ans = A * B * inv(A);
	for(int i=1;i<=n;i++) {
		printf("%d ",ans[i]);
	}
}

