	if (i == 0) return a0;
	if (i == 1) return a1;
	if (i == 2) return combine(a0, a1);
	if (mem.count(i)) return mem[i];
	return mem[i] = combine(calc(i - 2), calc(i - 1));
	if (i & 1) {
		vector<int> A = calc(i / 2 + 1);
		vector<int> B = calc(i / 2);
		return mem[i] = combine(combine(B, A), combine(B, A));
	} else {
		vector<int> A = calc(i / 2);
		vector<int> B = calc(i / 2 - 1);
		vector<int> C = calc(i / 2 + 1);
		return mem[i] = combine(combine(B, C), A);
	}
}

