        ;
    }

    Permutation(int a):sz(a){
        x.resize(sz);
        for(int i = 0;i < sz;i++){
            x[i]=i;
        }
    }

    int &operator[](int a){
        return x[a];
    }

    Permutation &operator=(const Permutation &a){
        sz = a.sz;
        x.resize(sz);
        for(int i = 0;i < sz;i++){
            x[i]=a.x[i];
        }
        return (*this);
    }

    void show(){
        for(int i =0 ;i<sz-1;i++){
            cout<<x[i]+1<<" ";
        }
        cout<<x[sz-1]+1<<endl;
    }



    Permutation operator *(const Permutation &B){
        Permutation ret(sz);
        for(int i = 0;i < sz;i++){
            ret.x[i]=x[B.x[i]];
        }
        return ret;
    }


    Permutation operator *(ll X){
        Permutation ret(sz);
        Permutation A = *this;
        while(X>0){
            if(X%2==1)ret = ret*A;
            A=A*A;
            X>>=1;
        }
        return ret;
    }
};

Permutation inv(const Permutation &A){
    Permutation ret(A.sz);
    for(int i = 0;i < A.sz;i++){
        ret[A.x[i]]=i;
    }
    return ret;
}

