    for(auto &e : u) e = 3 - e;
    reverse(u.begin(), u.end());
    reverse(w.begin(), w.end());
    while(u.size() && w.size() && u.back() + w.back() == 3) w.pop_back(), u.pop_back();
    reverse(w.begin(), w.end());
    u.insert(u.end(), w.begin(), w.end());
    v.push_back(u);
    // dump(i, v[i]);
  }

  auto r = getE();
  if(k <= 10) {
    for(int i = 0; i < v[k].size(); i++) r = op(r, x[v[k][i]]);
  } else {
    int c = 2 + 2 * ((k - 6) / 3);
    int len = 6 + 4 * ((k - 5) / 3);
    switch(k % 3) {
      case 0:
        len += 1;
