void init(permutation& a) {
	for (int i = 1; i <= n; ++i) a.p[i] = i;
}
permutation mul(const permutation& a, const permutation& b) {
	permutation c;
	for (int i = 1; i <= n; ++i) c.p[i] = a.p[b.p[i]];
	return c;
}
permutation mul(const permutation a, const permutation b, const permutation c) {
	return mul(mul(a, b), c);
}
permutation inv(const permutation& a) {
	permutation c;
	for (int i = 1; i <= n; ++i) c.p[a.p[i]] = i;
	return c;
}
permutation pow(permutation a, int k) {
	permutation c;
	init(c);
	for (; k; a = mul(a, a), k >>= 1) if (k & 1) c = mul(c, a);
	return c;
}

